var log = {
    info : function (info){
        console.log("Info: " + info);
    },
    obj : function(obj) {
        console.log("------------");
        console.log("Array content: ");
        console.log(obj);
        console.log("------------");
    },
    warning : function(warning) {
        console.log("Warning: " + warning);
    },
    error : function(error) {
        console.log("Error: " + error);
    }
}

module.exports = log